mapper.select:
select u.userid,u.username,u.nickname,u.password,u.email,u.userphoto,u.phone, u.mobile,u.deptid,u.roleid,u.state,u.sex,u.dr,u.create_time,u.create_time, u.update_id,u.update_time,u.note, d.deptname,r.rolename,u.deptgroup from hj_user u left join hj_dept d on d.deptid = u.deptid left join hj_role r on r.roleid = u.roleid where 1=1 and u.dr = 0
and u.phone = #{phone}
and u.email = #{email}
ORDER BY u.create_time DESC
